const path = require('path');
const HtmlBundlerPlugin = require('@test/html-bundler-webpack-plugin');

module.exports = {
  mode: 'production',

  output: {
    path: path.join(__dirname, 'dist/'),
    // add a publicPath to avoid JS error generated by Webpack: "Automatic publicPath is not supported in this browser"
    publicPath: '', // by importing an image in the JS file, the publicPath must not be auto
  },

  plugins: [
    new HtmlBundlerPlugin({
      entry: {
        index: './src/index.html',
      },
    }),
  ],

  module: {
    rules: [
      {
        test: /\.css$/,
        use: ['css-loader'],
      },
      {
        test: /\.svg/i,
        oneOf: [
          // import SVG in JS as raw content using `?raw` query
          {
            resourceQuery: /raw/,
            type: 'asset/source',
          },
          // fallback to default behavior:
          // import SVG in JS as output filename
          {
            type: 'asset/resource',
            generator: {
              filename: 'img/[name].[hash:8][ext]',
            },
          },
        ],
      },
    ],
  },
};
